/**
 * happy new year
 * new date
 */
var ct;
ct = setInterval(showTime, 1000);

function showTime() {
	var odate = new Date();
	var _date = odate.getDate(),
		_hours = odate.getHours(),
		_month = odate.getMonth() + 1,
		_min = odate.getMinutes(),
		_mind = odate.getSeconds();
	document.getElementById("timemy").innerText = _month + "月" + _date + "号" + _hours + "点" + _min + "分" + _mind + " 秒";
	document.getElementById("timemy").style.fontSize = "16px";
	document.getElementById("timemy").style.transition = "1s"
}
showTime();
var sh;
sh = setInterval(FreshTime, 1000);

function FreshTime() {
	var endtime = new Date("2020/01/01,00:00:00");
	var nowtime = new Date();
	var lefttime = parseInt((endtime.getTime() - nowtime.getTime()) / 1000);
	d = parseInt(lefttime / (24 * 60 * 60));
	h = parseInt(lefttime / (60 * 60) % 24);
	m = parseInt(lefttime / 60 % 60);
	s = parseInt(lefttime % 60);
	document.getElementById("LeftTime").innerHTML = d + "天" + h + "小时" + m + "分" + s + "秒";
	document.getElementById("LeftTime").style.textAlign = "center";
	var oimg = document.createElement("img");
	if (lefttime <= 59) {
		document.getElementById("wmtime").innerText = s;
		document.getElementById("wmtime").style.fontSize = "48px"
	}
	if (lefttime <= 30) {
		document.getElementById("wmtime").innerText = "准备好你的酒杯！！！";
		document.getElementById("wmtime").style.fontSize = "30px"
	}
	if (lefttime <= 20) {
		document.getElementById("wmtime").innerText = s;
		document.getElementById("wmtime").style.fontSize = "48px"
	}
	if (lefttime <= 0) {
		document.getElementById("LeftTime").innerHTML = "新年快乐";
		document.getElementById("LeftTime").style.color = "red";
		document.getElementById("LeftTime").style.fontSize = "24px";
		document.getElementById("outT").innerHTML = "新的一年到了";
		document.getElementById("wmtime").appendChild(oimg);
		document.getElementById("wmtime").appendChild(oimg).setAttribute("src", "img/xc_img1.jpg");
		document.getElementById("wmtime").style.animation = "time 1s";
		document.getElementById("wmtime").style.fontSize = "0px";
		clearInterval(sh)
	}
}
FreshTime();
